import React, { useState } from "react";
import API from "../utils/API";

const ViewStats = (props) => {

    const [playerState, setPlayerState] = useState({
        id: "",
        player: "",
        wins: ""
    })

    const showData = (evt) => {
        evt.preventDefault();
        API.getStats()
        .then(res => getId(playerState.player))
        .catch(err => console.log(err));
    }

    const updateStats = (player) => {
        API.saveStats(player);
    }

    const playerStats1 = () => {
        API.getPlayerStats(props.playerId)
        .then(res => setPlayerState({ id: res._id, player: props.playerId, wins: res.wins }))
    }
    playerStats1();

    return (
        <div>
            <h3>Player Stats</h3>
            <p>Player Name {playerState.player}</p>
            <p>Wins {playerState.wins}</p>
            <button onClick={showData}>Data</button>
        </div>
    )
}

export default ViewStats;
