import React, { useState } from "react";
import API from "../utils/API";

const ViewStats = (props) => {

    const [playerState, setPlayerState] = useState({
        player: "",
        wins: ""
    })

    const showData = (evt) => {
        evt.preventDefault();
        API.getStats()
        .then(res => updateStats())
        .catch(err => console.log(err));
    }

    const updateStats = () => {
        API.saveStats({ player: props.playerId, wins: props.wins });
    }

    const playerStats = () => {
        API.getPlayerStats()
        .then(res => setPlayerState({ player: res.player, wins: props.wins }))
    }
    
    playerStats();
    return (
        <div>
            {}
            <h3>Player Stats</h3>
            <p>Player Name {playerState.player}</p>
            <p>Wins {playerState.wins}</p>
            <button onClick={showData}>Data</button>
        </div>
    )
}

export default ViewStats;
