import React, { useState } from "react";
import API from "../utils/API";

const ViewStats = () => {

    const [playerState, setPlayerState] = useState({
        player: "",
        wins: ""
    })

    const showData = (evt) => {
        evt.preventDefault();
        API.getStats()
        .then(res => updateStats())
        .catch(err => console.log(err));
    }

    const updateStats = () => {
        API.saveStats();
    }

    const playerStats = () => {
        API.getPlayerStats()
        .then(res => setPlayerState({ player: "MG2020#1853", wins: res.wins }))
    }

    return (
        <div>
            <h3>Player Stats</h3>
            <p>Player Name {playerState.player}</p>
            <p>Wins {playerState.wins}</p>
            <button onClick={showData}>Data</button>
        </div>
    )
}

export default ViewStats;
