import React, { useState } from "react";
import ViewStats from "./ViewStats";
import API from "../utils/API";



const MainScreen = () => {
    const [developerState, setDeveloperState] = useState({
        playerId: "",
        wins: "",
        showStats: false
      });
    
    const [playerState, setPlayerState] = useState({
        player: "",
        wins: ""
    });

    const handleFormChange = (evt) => {
        setDeveloperState({ ...developerState, playerId: evt.target.value })
    }
    
    const viewStatsBtn = evt => {
        evt.preventDefault();
        setDeveloperState({ ...developerState, showStats: true })
        playerStats();
    }

    const playerStats = () => {
        API.getPlayerStats({ player: developerState.playerId })
        .then(res => setPlayerState({ ...playerState, wins: res.wins }))
    }
    


    if(!developerState.showStats)
    return (
        <div>
            <h1> Main Screen Homies!</h1>
            <form>
                <input onChange={evt => handleFormChange(evt)} placeholder={"MG2020#1853"} player={developerState.playerId} />
                <button onClick={viewStatsBtn}>View Stats</button>
            </form>
        </div>
    )

    return (
        <div>
            <h1> Main Screen Homies!</h1>
            <form>
                <input onChange={evt => handleFormChange(evt)} placeholder={"MG2020#1853"} />
                <button onClick={viewStatsBtn}>View Stats</button>
            </form>
            <ViewStats playerId={developerState.playerId} />
            
        </div>
        
    )
}

export default MainScreen;
