import React, { useState, useEffect } from "react";
import API from "../utils/API";


const LoginScreen = () => {

    const [developerState, setDeveloperState] = useState({
        username: "",
        password: "",
        errors: []
      });

    const [userPassState, setUserPassState] = useState({
        user: "",
        pass: ""
    })

    useEffect(() => {
        loadBooks()
      }, [])
      
    const handleUserChange = evt => {
        const userInput = evt.target.value;
        return setDeveloperState({ ...developerState, username: userInput });
    }

    const handlePasswordChange = evt => {
        const passInput = evt.target.value;
        return setDeveloperState({ ...developerState, password: passInput })
    }

    const submitForm = evt => {
        evt.preventDefault();

        let dataSubmit = {
            username: developerState.username,
            password: developerState.password
        }

        console.log("username: ", dataSubmit.username);
    }

    const loadUser = () => {
        API.getUser()
            .then(res => console.log(res))
                
                
                
            .catch(err => console.log(err));
            // setUserPassState({ user: res.data.username, pass: res.data.password});
    }

    const pullData = (evt) => {
        evt.preventDefault();
        loadUser();
    }

    // useEffect(() => {
    //     loadUserpass()
    // }, [])
    
    return (
        <form>
            <div>
                <p>UserName</p>
                <input onChange={handleUserChange} value={developerState.username}></input>
            </div>
            <div>
                <p>Password</p>
                <input onChange={handlePasswordChange} value={developerState.password}></input>
            </div>

            <div>
                <button onClick={submitForm}>Login</button>
            </div>
            <div>
                <button onClick={pullData}>Showpass</button>
            </div>
        </form>
    )
}

export default LoginScreen;
